def ccw(A, B, C):
    return (B[0]-A[0])*(C[1]-A[1]) - (B[1]-A[1])*(C[0]-A[0])

def segments_intersect(A, B, C, D):
    # Line AB: A + t*(B-A)
    # Line CD: C + s*(D-C)
    # Compute denominator
    denominator = (B[0] - A[0]) * (D[1] - C[1]) - (B[1] - A[1]) * (D[0] - C[0])
    if denominator == 0:
        return False  # parallel lines, no intersection
    # Compute numerator for t and s
    numerator_t = (C[0] - A[0]) * (D[1] - C[1]) - (C[1] - A[1]) * (D[0] - C[0])
    numerator_s = (C[0] - A[0]) * (B[1] - A[1]) - (C[1] - A[1]) * (B[0] - A[0])
    t = numerator_t / denominator
    s = numerator_s / denominator
    # Check if t and s are between 0 and 1
    return (0 <= t <= 1) and (0 <= s <= 1)

