        x, y = map(int, lines[i].strip().split())
        bases.append((x, y))
    return rebels, bases

def orientation(p, q, r):
    """Calculate the orientation of three points in the plane."""
    val = (q[1] - p[1]) * (r[0] - q[0]) - (q[0] - p[0]) * (r[1] - q[1])
    if val == 0:
        return 0  # Collinear
    elif val > 0:
        return 1  # Clockwise
    else:
        return 2  # Counterclockwise

def intersect_segments(p1, q1, p2, q2):
    """Check if two line segments intersect."""
    o1 = orientation(p1, q1, p2)
    o2 = orientation(p1, q1, q2)
    o3 = orientation(p2, q2, p1)
    o4 = orientation(p2, q2, q1)

    if (o1 != o2 and o3 != o4):
        return True

    if (o1 == 0 and on_segment(p1, p2, q1)):
        return True
    if (o2 == 0 and on_segment(p1, q2, q1)):
        return True
    if (o3 == 0 and on_segment(p2, p1, q2)):
        return True
    if (o4 == 0 and on_segment(p2, q1, q2)):
        return True

    return False

def on_segment(p, q, r):
    """Check if point q lies on segment pr."""
    if (q[0] <= max(p[0], r[0]) and q[0] >= min(p[0], r[0]) and
            q[1] <= max(p[1], r[1]) and q[1] >= min(p[1], r[1])):
        return True
    return False

def convex_hull(points):
    """Find the convex hull of a set of points."""
    n = len(points)
    if n < 3:
        return points

    points.sort(key=lambda x: (x[0], x[1]))
    hull = []
    l = 0
    for i in range(1, n):
        while len(hull) > 1 and orientation(hull[-2], hull[-1], points[i]) != 2:
            hull.pop()
        hull.append(points[i])
    l = len(hull)
    for i in range(n - 1, 0, -1):
        while len(hull) > l and orientation(hull[-2], hull[-1], points[i]) != 2:
            hull.pop()
        hull.append(points[i])
    return hull

def solve(rebels, bases):
    """Solve the problem."""
    all_points = rebels + bases
    hull = convex_hull(all_points)

    rebels_in_hull = [p for p in rebels if p in hull]
    bases_in_hull = [p for p in bases if p in hull]

    if len(rebels_in_hull) < len(bases_in_hull):
        return "No"
    elif len(rebels_in_hull) == len(bases_in_hull):
        for i in range(len(rebels_in_hull)):
            for j in range(i + 1, len(rebels_in_hull)):
                if intersect_segments(rebels_in_hull[i], bases_in_hull[i],
                                      rebels_in_hull[j], bases_in_hull[j]):
                    return "No"
        return "Yes"
    else:
        return "No"

def main():
    """Main function."""
    rebels, bases = read_input()
    result = solve(rebels, bases)
    print(result)

