def main():
    data = sys.stdin.read().split()
    n, x = int(data[0]), int(data[1])
    a = list(map(int, data[2:n+2]))
    b = list(map(int, data[n+2:2*n+2]))

    participants = []
    for ai in a:
        for bi in b:
            s = ai + bi
            if s >= x:
                participants.append(s)

    # To find all possible s, we need to consider all possible pairs where ai + bj >=x
    # So, generate all possible s for any (ai, bj) where ai is from a and bj is from b
    possible_s = set()
    for ai in a:
        for bi in b:
            if ai + bi >= x:
                possible_s.add(ai + bi)
    possible_s = sorted(possible_s)

    # Now, for each s in possible_s, calculate count_gt and count_eq
    from collections import defaultdict
    freq = defaultdict(int)
    for ai in a:
        for bi in b:
            s_total = ai + bi
            freq[s_total] += 1

    best_rank = n + 1
    worst_rank = n + 1
    for s in possible_s:
        count_gt = 0
        count_eq = 0
        for key in freq:
            if key > s:
