    n, m = map(int, lines[0].split())
    graph = [[] for _ in range(n)]
    for i in range(1, m + 1):
        u, v = map(int, lines[i].split())
        graph[u - 1].append(v - 1)
        graph[v - 1].append(u - 1)
    return n, graph

def find_cycle(graph, start):
    visited = [False] * len(graph)
    stack = deque([start])
    parent = [-1] * len(graph)
    while stack:
        node = stack.pop()
        if not visited[node]:
            visited[node] = True
            for neighbor in graph[node]:
                if neighbor != parent[node]:
                    parent[neighbor] = node
                    stack.append(neighbor)
        elif parent[node] != -1:
            cycle_start = node
            while cycle_start != start:
                cycle_start = parent[cycle_start]
            return True, list(range(cycle_start, node + 1)) + list(range(node, cycle_start, -1))
    return False, []

def find_odd_cycles(graph):
    odd_cycles = []
    for i in range(len(graph)):
        has_cycle, cycle = find_cycle(graph, i)
        if has_cycle and len(cycle) % 2 == 1:
            odd_cycles.append(cycle)
    return odd_cycles

def find_minimal_odd_cycles(graph):
    odd_cycles = find_odd_cycles(graph)
    if not odd_cycles:
        return 1, 0
    min_length = min(len(cycle) for cycle in odd_cycles)
    minimal_cycles = [cycle for cycle in odd_cycles if len(cycle) == min_length]
    return min_length - 1, len(minimal_cycles)

