import sys

def minimal_d(n, l, a):
    a = sorted(a)
    left = 0.0
    right = float('inf')
    # Binary search boundaries
    for i in range(n):
        if a[i] - left > 1e-9:
            left = a[i] - (right - left)/2 + left
        else:
            left = max(left, a[i] - (right - left)/2)
        if a[i] + (right - left)/2 > right:
            right = a[i] + (right - left)/2
        else:
            right = min(right, a[i] + (right - left)/2)

    # Now perform binary search between left and right
    eps = 1e-10
    for _ in range(100):
        mid = (left + right) / 2
        covered = 0.0
        max_reach = 0.0
        for pos in a:
            start = max(pos - mid, 0.0)
            end = min(pos + mid, l)
            if start > covered:
                covered = start
            if end > max_reach:
                max_reach = end
            if max_reach >= l:
                break
        if max_reach >= l - eps:
            right = mid
        else:
            left = mid
    return round(right, 10)

