        for ai in a:
            start = max(0, ai - d)
            end = min(l, ai + d)
            if start > prev_end:
                return False
            prev_end = end
        return prev_end >= l

    low = 0.0
    high = l
    eps = 1e-10
    for _ in range(100):
        mid = (low + high) / 2
        if is_covered(mid):
            high = mid
        else:
            low = mid
    print("{0:.10f}".format(high))

