    return n, l, a

def calculate_d(n, l, a):
    a.sort()
    min_d = 0.0
    max_dist = 0.0

    for i in range(n):
        if i == 0:
            max_dist = max(max_dist, l - a[i])
        elif i == n - 1:
            max_dist = max(max_dist, a[i] - 0)
        else:
            max_dist = max(max_dist, min(a[i] - a[i-1], a[i+1] - a[i]))

    min_d = max_dist / 2.0
    return min_d

def main():
    n, l, a = read_input()
    d = calculate_d(n, l, a)
    print(f"{d:.10f}")

if __name__ == "__main__":
    main()

