import sys

def gcd(a, b):
    """Compute the greatest common divisor of a and b"""
    while b:
        a, b = b, a % b
    return a

def lcm(a, b):
    """Compute the lowest common multiple of a and b"""
    return a * b // gcd(a, b)

def solve():
    n = int(input())
    m = len(input().strip())
    k = len(input().strip())

    # Calculate the number of rounds each sequence is repeated
    repeat = n // (m + k - gcd(m, k))
    # Calculate the remaining rounds after the last repetition
    remaining = n % (m + k - gcd(m, k))

    nikephoros = 0
    polycarpus = 0

    for i in range(1, min(m, k) + 1):
        if input().strip()[i-1] != input().strip()[-i]:
            if input().strip()[i-1] == 'R' and input().strip()[-i] == 'S':
                nikephoros += 1
            elif input().strip()[i-1] == 'S' and input().strip()[-i] == 'P':
                nikephoros += 1
            elif input().strip()[i-1] == 'P' and input().strip()[-i] == 'R':
                nikephoros += 1
            else:
                polycarpus += 1

    for _ in range(repeat):
        for i in range(m):
            if input().strip()[i] != input().strip()[(i+k-1)%k]:
                if input().strip()[i] == 'R' and input().strip()[(i+k-1)%k] == 'S':
                    nikephoros += 1
                elif input().strip()[i] == 'S' and input().strip()[(i+k-1)%k] == 'P':
                    nikephoros += 1
                elif input().strip()[i] == 'P' and input().strip()[(i+k-1)%k] == 'R':
                    nikephoros += 1
                else:
                    polycarpus += 1

    for i in range(min(m, k), remaining):
        if input().strip()[i] != input().strip()[(i+k-1)%k]:
            if input().strip()[i] == 'R' and input().strip()[(i+k-1)%k] == 'S':
                nikephoros += 1
            elif input().strip()[i] == 'S' and input().strip()[(i+k-1)%k] == 'P':
                nikephoros += 1
            elif input().strip()[i] == 'P' and input().strip()[(i+k-1)%k] == 'R':
                nikephoros += 1
            else:
                polycarpus += 1

    print(nikephoros, polycarpus)

solve()

