        elif left[remaining_left[i] - 1] == right[remaining_right[j] - 1]:
            pairs.append((remaining_left[i], remaining_right[j]))
            i += 1
            j += 1
        elif left[remaining_left[i] - 1] < right[remaining_right[j] - 1]:
