import sys

def read_input():
    """Reads input from standard input."""
    lines = sys.stdin.readlines()
    n, m = map(int, lines[0].split())
    edges = []
    for i in range(1, m + 1):
        a, b, c = map(int, lines[i].split())
        edges.append((a, b, c))
    return n, m, edges

def dfs(v, parent, visited, graph, edges, flow):
    """Performs a depth-first search to find augmenting paths."""
    visited[v] = True
    for u, c in graph[v]:
        if not visited[u]:
            if dfs(u, v, visited, graph, edges, flow):
                edges[v - 1][2] = c
                edges[u - 1][2] = 0
                flow += c
                return True
        elif edges[u - 1][2] < c:
            edges[v - 1][2] = c - edges[u - 1][2]
            edges[u - 1][2] += c
            flow += edges[v - 1][2]
            return True
    return False

def max_flow(n, m, edges):
    """Finds the maximum flow using the Ford-Fulkerson algorithm."""
    graph = [[] for _ in range(n)]
    for u, v, c in edges:
        graph[u - 1].append((v - 1, c))
        graph[v - 1].append((u - 1, c))
    flow = 0
    while True:
        visited = [False] * n
        if not dfs(0, -1, visited, graph, edges, flow):
            break
    return flow

def solve(n, m, edges):
    """Solves the problem by finding the maximum flow and determining edge directions."""
    max_flow_value = max_flow(n, m, edges)
    result = []
    for u, v, c in edges:
        if max_flow_value == c:
            result.append('1')
        else:
            result.append('0')
    return '\n'.join(result)

def main():
    n, m, edges = read_input()
    print(solve(n, m, edges))

