import sys

def read_input():
    """Reads input from standard input."""
    lines = sys.stdin.readlines()
    n, m = map(int, lines[0].split())
    edges = []
    for i in range(1, m + 1):
        a, b, c = map(int, lines[i].split())
        edges.append((a, b, c))
    return n, m, edges

def solve(n, m, edges):
    """Solves the problem and returns the result."""
    graph = [[] for _ in range(n + 1)]
    for u, v, c in edges:
        graph[u].append((v, c))
        graph[v].append((u, c))

    parent = [-1] * (n + 1)
    stack = [1]
    while stack:
        u = stack.pop()
        for v, c in graph[u]:
            if parent[v] == -1:
                parent[v] = u
                stack.append(v)

    # Reconstruct the flow direction
    flow_direction = [[0] * len(edges) for _ in range(n + 1)]
    for i, (u, v, _) in enumerate(edges):
        if parent[v] == u:
            flow_direction[u][i] = 1
        else:
            flow_direction[v][i] = 1

    return flow_direction

def print_result(flow_direction):
    """Prints the result to standard output."""
    for row in flow_direction[1:-1]:
        print(' '.join(map(str, row)))

