class Souvenir:
    def __init__(self, weight, cost):
        self.weight = weight
        self.cost = cost

def max_cost(souvenirs, capacity):
    souvenirs.sort(key=lambda x: (x.weight, -x.cost))
    dp = [[0] * (capacity + 1) for _ in range(len(souvenirs) + 1)]

    for i in range(1, len(souvenirs) + 1):
        for j in range(1, capacity + 1):
            if souvenirs[i - 1].weight <= j:
                dp[i][j] = max(dp[i - 1][j], dp[i - 1][j - souvenirs[i - 1].weight] + souvenirs[i - 1].cost)
            else:
                dp[i][j] = dp[i - 1][j]

    return dp[-1][-1]

