class MaxCostSolver:
    def __init__(self, n, m):
        self.n = n
        self.m = m
        self.souvenirs = []
        self.dp = [[0] * (m + 1) for _ in range(n + 1)]

    def solve(self):
        for i in range(1, self.n + 1):
            weight, cost = self.souvenirs[i - 1].weight, self.souvenirs[i - 1].cost
            for j in range(self.m + 1):
                if weight > j:
                    self.dp[i][j] = self.dp[i - 1][j]
                else:
                    self.dp[i][j] = max(self.dp[i - 1][j], self.dp[i - 1][j - weight] + cost)
        return self.dp[self.n][self.m]

