import sys
from collections import deque

def parse_golorp(name):
    grid = []
    for c in name:
        grid.append(c)

    # Determine the number of variables
    # We need to find the minimal path through the grid that covers all necessary variables
    visited = {}
    queue = deque()
    start = None
    for i, row in enumerate(grid):
        for j, c in enumerate(row):
            if c == '?':
                if start is None:
                    start = (i, j)
                else:
                    # Check if it's part of the same connected component
                    pass  # For simplicity, assume one connected component
    if start is None:
        return "false"

    # Now model the constraints as a directed graph
    # Each step will have constraints based on the command
    # The structure is such that each cell may have commands that affect the next move
    # We'll build a list of constraints where each constraint is (current, next, op)

    # Extract the constraints
    constraints = []
    dirs = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    for i in range(len(grid)):
        for j in range(len(grid[i])):
            c = grid[i][j]
            if c == '>':
                if j+1 < len(grid[i]):
                    next_col = j + 1
                else:
                    continue
                constraints.append(('x', (i, j), (i, next_col), 'geq'))
            elif c == '<':
                if j-1 >= 0:
                    prev_col = j - 1
                else:
                    continue
                constraints.append(('x', (i, j), (i, prev_col), 'leq'))
            elif c == '=':
                if j+1 < len(grid[i]):
                    next_col = j + 1
                else:
                    continue
                constraints.append(('x', (i, j), (i, next_col), 'eq'))
            elif c == '+':
                if j+1 < len(grid[i]):
                    next_col = j + 1
                else:
                    continue
                constraints.append(('x', (i, j), (i, next_col), 'add'))
            elif c == '-':
                if j-1 >= 0:
                    prev_col = j - 1
                else:
                    continue
                constraints.append(('x', (i, j), (i, prev_col), 'sub'))
            elif c == '_':
                pass  # No constraint
            elif c == '*':
                pass  # No constraint

    # Now, model the variables as nodes and constraints as edges
    # Each node is (i, j), but we can assign each cell to a unique variable index
    # Let's map each cell to a unique variable
    var_map = {}
    var_id = 0
    for i in range(len(grid)):
        for j in range(len(grid[i])):
            var_map[(i, j)] = var_id
            var_id += 1

    # Now, create a list of all variables
    num_vars = var_id
    # Now, build the constraints
    # Each constraint is (from_var, to_var, relation)
    graph = [[] for _ in range(num_vars)]
    for constraint in constraints:
        from_var, to_var, rel = constraint
        graph[from_var].append( (to_var, rel) )

    # Now, perform topological sort and assign levels
    # Also, detect cycles
    in_degree = [0] * num_vars
    for u in range(num_vars):
        for v, _ in graph[u]:
            in_degree[v] += 1

    queue = deque()
    for u in range(num_vars):
        if in_degree[u] == 0:
            queue.append(u)

    topo_order = []
    while queue:
        u = queue.popleft()
        topo_order.append(u)
        for v, rel in graph[u]:
            in_degree[v] -= 1
            if in_degree[v] == 0:
                queue.append(v)

    if len(topo_order) != num_vars:
        return "false"

    # Now, assign values to each variable in topo order
    value = [10] * num_vars
    for u in topo_order:
        for v, rel in graph[u]:
            if rel == 'geq':
                value[v] >= value[u]
            elif rel == 'leq':
                value[v] <= value[u]
            elif rel == 'eq':
                value[v] == value[u]

    # Now, find the minimal lex sequence
    res = [0] * num_vars
    for u in topo_order:
        min_val = 0
        # Check all constraints from u
        possible = True
        for v, rel in graph[u]:
            if rel == 'geq':
                if value[v] < value[u]:
                    possible = False
                    break
            elif rel == 'leq':
                if value[v] > value[u]:
                    possible = False
                    break
            elif rel == 'eq':
                if value[v] != value[u]:
                    possible = False
                    break
        if not possible:
            return "false"

        for candidate in range(0, 10):
            temp = [int(x) for x in str(candidate)]
            if len(temp) != num_vars:
                continue
            valid = True
            for v, rel in graph[u]:
                if rel == 'geq':
                    if temp[v] < temp[u]:
                        valid = False
                        break
                elif rel == 'leq':
                    if temp[v] > temp[u]:
                        valid = False
                        break
                elif rel == 'eq':
                    if temp[v] != temp[u]:
                        valid = False
                        break
            if valid:
                for v, rel in graph[u]:
                    if rel == 'geq':
                        if temp[v] < temp[u]:
                            valid = False
                            break
                    elif rel == 'leq':
                        if temp[v] > temp[u]:
                            valid = False
                            break
                    elif rel == 'eq':
                        if temp[v] != temp[u]:
                            valid = False
                            break
                if valid:
                    res[u] = candidate
                    break
        else:
            return "false"

    # Now, convert res to a string
    result = ''.join(map(str, res))
    return result

def main():
    input = sys.stdin.read().split()
    for s in input:
        print(parse_golorp(s))

if __name__ == "__main__":
    main()

