from collections import deque

def main():
    s = sys.stdin.read().strip()
    stack = []
    registers = {}
    pos = 0
    direction = 1  # 1 for right, -1 for left
    output = []

    for c in s:
        if c == '>':
            pos += direction
        elif c == '<':
            pos -= direction
        elif c == '+':
            next_pos = pos + direction
            if next_pos >= len(s):
                stack.append(('+', registers.get(pos, 0), registers.get(next_pos, 0)))
                break
            if next_pos < 0:
                stack.append(('+', registers.get(pos, 0), 0))
                break
            res = registers.get(pos, 0) + registers.get(next_pos, 0)
            registers[next_pos] = res
            pos = next_pos
        elif c == '-':
            next_pos = pos + direction
            if next_pos >= len(s):
                stack.append(('-', registers.get(pos, 0), registers.get(next_pos, 0)))
                break
            if next_pos < 0:
                stack.append(('-', registers.get(pos, 0), 0))
                break
            res = registers.get(pos, 0) - registers.get(next_pos, 0)
            registers[next_pos] = res
            pos = next_pos
        elif c == '*':
            next_pos = pos + direction
            if next_pos >= len(s):
                stack.append(('*', registers.get(pos, 0), registers.get(next_pos, 0)))
                break
            if next_pos < 0:
                stack.append(('*', registers.get(pos, 0), 0))
                break
            res = registers.get(pos, 0) * registers.get(next_pos, 0)
            registers[next_pos] = res
            pos = next_pos
        elif c == '.':
            output.append(str(registers.get(pos, 0)))
        elif c in '0123456789':
            registers[pos] = int(c)
        else:
            next_pos = pos + direction
            if next_pos >= len(s):
                stack.append((c, registers.get(pos, 0), registers.get(next_pos, 0)))
                break
            if next_pos < 0:
                stack.append((c, registers.get(pos, 0), 0))
                break
            try:
                if c == '_':
                    pass
                elif c == '/':
                    current = registers.get(pos, 0)
                    next_val = registers.get(next_pos, 0)
                    if next_val == 0:
                        raise ZeroDivisionError
                    res = current // next_val
                    registers[next_pos] = res
                    pos = next_pos
                else:
                    pass
            except:
                print("false")
                return
            pos = next_pos

    if stack:
        print("false")
        return

    if not output:
        var_order = list(registers.keys())
        var_order.sort(key=lambda x: str(x))
        min_vars = var_order[:10]
        min_vals = [str(registers[v]) for v in min_vars]
        print(''.join(min_vals))
    else:
        print(''.join(output))

