import sys

def compute_sum(m, a, b):
    d = a
    if b != 0:
        d = gcd(a, b)
    max_reach = 0
    count = 0
    for x in range(m + 1):
        if x >= a:
            max_reach = min(max_reach + a, x)
            steps = (max_reach - (max_reach % d)) // d + 1
            count += steps
        else:
            steps = 1
            count += steps
    return count

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

