from collections import deque

def main():
    n, k = map(int, sys.stdin.readline().split())
    edges = [[] for _ in range(n+1)]
    for _ in range(n-1):
        a, b = map(int, sys.stdin.readline().split())
        edges[a].append(b)
        edges[b].append(a)

    result = [i for i in range(1, n+1)]
    visited = [False] * (n + 1)
    q = deque()
    start = n
    visited[start] = True
    q.append(start)
    excluded = set()

    while q:
        u = q.popleft()
        for v in edges[u]:
            if not visited[v]:
                visited[v] = True
                q.append(v)

    for i in range(1, n+1):
        if not visited[i]:
            excluded.add(i)

    to_remove = []
    for i in sorted(result):
        if i not in excluded:
            to_remove.append(i)

    print(' '.join(map(str, to_remove[:k])))

