            child_path = current_node['parent']['path'] + bit
            if bit not in current_node['children']:
                new_child = {
                    'parent': current_node,
                    'path': child_path,
                    'm': 0,
                    'children': {}
                }
                current_node['children'][bit] = new_child
                queue.append(new_child)

    # Collect all leaf nodes (strings of length n)
    leaves = []
    stack = [root]
    while stack:
        node = stack.pop()
        if len(node['path']) == n:
            leaves.append(node)
        else:
            for child in node['children'].values():
                stack.append(child)

    # Now, process each node in BFS order to calculate dp states
    # We'll process nodes level by level, starting from the root
    from collections import deque
    visited = set()
    queue = deque()
    queue.append((root, 0))
    visited.add(root)

