    while stack:
        node = stack.pop()
        all_nodes.append(node)
        for child in node['children'].values():
            stack.append(child)

    # Sort nodes by their path length
    all_nodes.sort(key=lambda x: len(x['path']))

    # DP state: node -> current sum -> count
    # But to make it manageable, we can represent for each node, the possible sums and their counts
    # However, for efficiency, we'll process nodes level by level and track the sum contributions
    # Initialize dp for root's children
    for child in root['children'].values():
        dp[child] = {}
        m_parent = root['m']
        for c in range(0, k+1):
