def add(a, b):
    return (a + b) % MOD

def mul(a, b):
    return (a * b) % MOD

def pow(a, b):
    res = 1
    while b:
        if b & 1:
            res = mul(res, a)
        a = mul(a, a)
        b >>= 1
    return res

class BIT:
    def __init__(self, n):
        self.nums = [0] * (n + 1)

    def update(self, i, delta):
        while i < len(self.nums):
            self.nums[i] = add(self.nums[i], delta)
            i += i & -i

    def query(self, i):
        res = 0
        while i:
            res = add(res, self.nums[i])
            i -= i & -i
        return res

def main():
    input_lines = sys.stdin.readlines()
    output = []
    for line in input_lines:
        n, k, f = map(int, line.split())
        bit = BIT(k + 1)
        dp = [[0] * (k + 1) for _ in range(n + 1)]
        dp[0][0] = 1
        for i in range(1, n + 1):
            for j in range(i):
                for c in range(k + 1):
                    dp[i][c] = add(dp[i][c], dp[j][min(c, k)] * bit.query(c))
            for c in range(k + 1):
                bit.update(c, dp[i - 1][c])
            for c in range(k + 1):
                dp[i][c] = mul(dp[i][c], pow(2, k - c))
        ans = 0
        for c in range(k + 1):
            ans = add(ans, dp[n][c] * (f >= c))
        output.append(str(ans))

