import sys

def read_input():
    return sys.stdin.readline().strip()

def read_ints():
    return list(map(int, read_input().split()))

def solve(n, m, a, b):
    odd = sum(1 for x in a if x % 2 == 1)
    even = n - odd
    keys = sorted(b)
    chests = sorted(a)

    opened = 0
    i = j = 0

    while i < odd and j < m:
        if keys[j] + chests[i] == 1:
            opened += 1
            i += 1
            j += 1
        elif keys[j] + chests[i] > 1:
            j += 1
        else:
            i += 1

    opened += min(even, m)

    return opened

