def min_tiles_needed(tiles):
    from collections import defaultdict

    # Function to count occurrences of each tile
    counts = defaultdict(int)
    for t in tiles:
        suit = t[1]
        num = int(t[0])
        counts[(suit, num)] += 1

    # Generate all possible triplets
    triplets = []
    for suit in ['m', 'p', 's']:
        for num in range(1, 10):
            triplets.append((suit, num, num, num))

    # Generate all possible sequences
    sequences = []
    for suit in ['m', 'p', 's']:
        for start in range(1, 8):
            sequences.append((suit, start, start+1, start+2))

    min_add = 4  # Maximum possible is 3, but initializing with 4 to find min

    # Check each possible mentsu
    for mentsu in triplets + sequences:
        suit_m, num1_m, num2_m, num3_m = mentsu
        current_count = 0
        for tile in tiles:
            if tile[1] == suit_m and tile[0] == str(num1_m):
                current_count += 1
            elif tile[1] == suit_m and tile[0] == str(num2_m):
                current_count += 1
            elif tile[1] == suit_m and tile[0] == str(num3_m):
                current_count += 1
        # Calculate how many more are needed
        needed = 3 - current_count
        if needed < min_add:
            min_add = needed
            if min_add == 0:
                break  # Can't get better than 0

    return min_add

