def min_draws(group):
    from collections import defaultdict

    tiles = group.split()
    count = defaultdict(int)
    for t in tiles:
        count[t] += 1

    # Check for triplet
    for t in count:
        if count[t] >= 3:
            return 0

    # Check for shuntsu
    suits = defaultdict(list)
    for t in tiles:
        num = int(t[0])
        suits[t[1]].append(num)

    min_add = float('inf')
    for suit in suits:
        sorted_nums = sorted(suits[suit])
        for i in range(len(sorted_nums) - 2):
            a, b, c = sorted_nums[i], sorted_nums[i+1], sorted_nums[i+2]
            if b == a + 1 and c == b + 1:
                continue_sequence = False
                for j in range(len(sorted_nums)):
                    if sorted_nums[j] == a:
                        continue_sequence = True
                        break
                if continue_sequence:
                    # How many tiles are missing
                    required = [a, a+1, a+2]
                    current = set(sorted_nums)
                    needed = []
                    for r in required:
                        if r not in current:
                            needed.append(r)
                    draws_needed = len(needed)
                    if draws_needed < min_add:
                        min_add = draws_needed
    return min_add

