def compute_min_draws(tiles):
    suits = {'m', 'p', 's'}
    counts = {}
    for t in tiles:
        num = t[0]
        suit = t[1]
        counts[suit] = counts.get(suit, 0) + 1
    current_suits = {suit for suit, _ in counts.items()}

    # Check for existing triplet or sequence
    for suit in current_suits:
        numbers = []
        for tile in tiles:
            if tile[1] == suit:
                numbers.append(int(tile[0]))
        numbers.sort()
        if len(numbers) >= 3:
            if numbers[0] == numbers[1] == numbers[2]:
                return 0
            elif numbers[1] - numbers[0] == 1 and numbers[2] - numbers[1] == 1:
                return 0

    min_draws = float('inf')

    # Check all possible triplets
    for suit in suits:
        if suit not in counts:
            continue
        max_num = list(range(1, 10))
        for n in range(1, 10):
            if n in counts[suit]:
                remaining_needed = 2
                for i in range(n-2, -1, -1):
                    if i in counts[suit]:
                        remaining_needed -= 1
                        if remaining_needed <= 0:
                            break
                if remaining_needed <= 0:
                    total = 0
                    for num in range(n-2, n+1):
                        if num not in counts[suit]:
                            total += 1
                    if total < min_draws:
                        min_draws = total
        for n in range(9, 0, -1):
            if n in counts[suit]:
                remaining_needed = 2
                for i in range(n+1, n+3):
                    if i > 9:
                        break
                    if i in counts[suit]:
                        remaining_needed -= 1
                        if remaining_needed <= 0:
                            break
                if remaining_needed <= 0:
                    total = 0
                    for num in range(n+1, n+3):
                        if num not in counts[suit]:
                            total += 1
                    if total < min_draws:
                        min_draws = total

    # Check all possible sequences across different suits
    for suit1 in suits:
        for suit2 in suits:
            if suit1 == suit2:
                continue
            current_nums = []
            for tile in tiles:
                if tile[1] == suit1:
                    current_nums.append(int(tile[0]))
                elif tile[1] == suit2:
                    current_nums.append(int(tile[0]))
            current_nums.sort()
            for start in range(1, 8):
                end = start + 2
                if end > 9:
                    continue
                needed = 0
                found = 0
                for num in [start, start+1, start+2]:
                    if num in current_nums:
                        found += 1
                    else:
                        needed += 1
                if found == 3:
                    return 0

    return min_draws if min_draws != float('inf') else 4

