def check_hand(hand):
    """
    Check if a hand contains a koutsu or shuntsu.

    Args:
        hand (list): A list of strings representing the tiles in the hand.

    Returns:
        int: The minimum number of extra suited tiles needed to draw. 0 if the hand already contains a koutsu or shuntsu.
    """
    # Create dictionaries to store the count of each suit and the maximum number in each suit
    suits = {'m': {}, 'p': {}, 's': {}}
    for tile in hand:
        num, suit = tile[0], tile[1]
        if num not in suits[suit]:
            suits[suit][num] = 0
        suits[suit][num] += 1

    # Check for koutsu
    for suit in suits.values():
        if any(count >= 3 for count in suit.values()):
            return 0

    # Check for shuntsu
    for suit in suits.values():
        nums = sorted(suit.keys())
        for i in range(len(nums) - 2):
            if nums[i + 1] - nums[i] == 1 and nums[i + 2] - nums[i + 1] == 1:
                return 0

    # If no koutsu or shuntsu is found, return the minimum number of extra suited tiles needed
    min_extra_tiles = 10
    for suit in suits.values():
        max_num = max(suit.keys())
        min_extra_tiles = min(min_extra_tiles, 10 - max_num)
    return min_extra_tiles


