def count_triangles(A, B, C, D):
    total = 0
    for z in range(C, D + 1):
        s = z - B + 1
        # Case 1: x >= max(A, s)
        lower_x_case1 = max(A, s)
        if lower_x_case1 > B:
            case1 = 0
        else:
            num_x_case1 = B - lower_x_case1 + 1
            if B > C:
                case1 = num_x_case1 * (C - B + 1)
            else:
                case1 = 0

        # Case 2: x <= min(s-1, B)
        x_max_case2 = min(s - 1, B)
        if x_max_case2 < A:
            case2 = 0
        else:
            m = (z + 1) // 2
            x_min_part1 = A
            x_max_part1 = min(m, x_max_case2)

            # Compute sum_part1
            if x_min_part1 > x_max_part1:
                sum_p1 = 0
            else:
                start = max(x_min_part1, z - C)
                if start > x_max_part1:
                    sum_p1 = 0
                else:
                    end = x_max_part1
                    a = z - end + 1
                    if a < 0:
                        sum_p1 = 0
                    else:
                        b = z - start + 1
                        if b < 0:
                            sum_p1 = 0
                        else:
                            num_terms = end - start + 1
                            sum_p1 = (b + a) * num_terms // 2

            # Compute sum_part2
            x_min_part2 = m + 1
            x_max_part2 = x_max_case2
            if x_min_part2 > x_max_part2:
                sum_p2 = 0
            else:
                start_p2 = x_min_part2
                end_p2 = x_max_part2
                if end_p2 > C:
                    end_p2 = C
                if start_p2 > end_p2:
                    sum_p2 = 0
                else:
                    a = C - end_p2 + 1
                    b = C - start_p2 + 1
                    num_terms = end_p2 - start_p2 + 1
                    sum_p2 = (a + b) * num_terms // 2

            case2 = sum_p1 + sum_p2

        total += case1 + case2

    return total

