def compute_original_lnds(arr):
    # Compute the LNDS of the original array
    dp = [1] * len(arr)
    for i in range(len(arr)):
        for j in range(i):
            if arr[j] <= arr[i]:
                dp[i] = max(dp[i], dp[j] + 1)
    return max(dp)

def reverse_and_compute(arr, l, r):
    # Reverse the subarray from l to r (0-based indices)
    new_arr = arr.copy()
    new_arr[l:r+1] = new_arr[r:l-1:-1]
    # Compute the LNDS of the new array
    dp = [1] * len(new_arr)
    for i in range(len(new_arr)):
        for j in range(i):
            if new_arr[j] <= new_arr[i]:
                dp[i] = max(dp[i], dp[j] + 1)
    return max(dp)

