    import sys
    input = sys.stdin.read().split()
    n = int(input[0])
    a = list(map(int, input[1:n+1]))

    # Compute original LNDS
    def compute_lnds(arr):
        dp = []
        for num in arr:
            left, right = 0, len(dp)
            while left < right:
                mid = (left + right) // 2
                if dp[mid] <= num:
                    left = mid + 1
                else:
                    right = mid
            dp.insert(left, num)
        return len(dp)

    original_lnds = compute_lnds(a)

    # Precompute prefix_ones: prefix_ones[l] is the number of 1s in a[0..l-1]
    prefix_ones = [0] * (n + 1)
    for i in range(1, n + 1):
        prefix_ones[i] = prefix_ones[i-1] + (a[i-1] == 1)

    # Precompute suffix_twos: suffix_twos[r] is the number of 2s in a[r..n-1]
    suffix_twos = [0] * (n + 1)
    for i in range(n-1, -1, -1):
        suffix_twos[i] = suffix_twos[i+1] + (a[i] == 2)

    max_lnds = original_lnds

    # Iterate over all possible l and r
    for l in range(1, n+1):
        for r in range(l, n+1):
            # Calculate the number of 1s in a[l-1 ... r-1]
            # which is prefix_ones[r] - prefix_ones[l-1]
            ones_in_sub = prefix_ones[r] - prefix_ones[l-1]
            twos_in_sub = (r - l + 1) - ones_in_sub
            # The number of 2s after r is suffix_twos[r+1]
            current_value = prefix_ones[l] + twos_in_sub + suffix_twos[r+1]
            if current_value > max_lnds:
                max_lnds = current_value

    print(max_lnds)

