def can_make_non_decreasing(A):
    m = sum(1 for x in A if x == 1)
    T = [1]*m + [2]*(len(A)-m)
    for k in range(len(A)+1):
        if k == 0:
            P = []
            Q = T[:k]
        else:
            P = A[:k]
            Q = T[k:]
        if P != T[:k]:
            continue
        reversed_Q = list(reversed(Q))
        for idx in range(len(A)):
            if idx < k:
                continue
            found = False
            for start in range(idx, -1, -1):
                end = idx - (start - idx)
                if end < 0:
                    break
                current_sub = A[start:end+1][::-1]
                if current_sub == reversed_Q:
                    found = True
                    break
            if not found:
                break
        else:
            return True
    return False

