    A = list(sys.stdin.readline().strip())

    # Convert A into a list of integers
    A = [int(c) for c in A]

    # Sort the digits in descending order
    sorted_digits = sorted(A, reverse=True)

    # Initialize DP table. dp[i][j] represents assigning first i digits to S1 and first j to S2
    dp = [[0]*(n+1) for _ in range(n+1)]

    # Fill dp table
    for i in range(1, n+1):
        for j in range(1, n+1):
            if i + j > 2*n:
                continue
            max_score = 0
            for k in range(i+j):
                if k < i:
                    s1_val = sorted_digits[k] * (10 ** (n - (i - k - 1)))
                else:
                    s1_val = 0
                if k < j:
                    s2_val = sorted_digits[k] * (10 ** (n - (j - k - 1)))
                else:
                    s2_val = 0
                current_score = s1_val + s2_val
                if current_score > max_score:
                    max_score = current_score
                # Check if the current digit assignment leads to a better state
                if dp[i][j] < max_score:
                    dp[i][j] = max_score

    # Now reconstruct the path
    # Starting from dp[n][n], find the last move
    moves = []
    i, j = n, n
    while i > 0 or j > 0:
        if i > 0 and j > 0:
            # Compare the next digit in S1 and S2
            if sorted_digits[0] > sorted_digits[1]:
                # Assign to S1
                moves.append('H')
                i -= 1
            else:
                moves.append('M')
                j -= 1
        elif i > 0:
            moves.append('H')
            i -= 1
        else:
            moves.append('M')
            j -= 1
    # Reverse to get the correct order
