def dp(nums, n):
    dp = [[0] * (n + 1) for _ in range(2)]
    path = [[[] for _ in range(n + 1)] for _ in range(2)]

    for i in range(1, len(nums)):
        for j in range(1, n + 1):
            if nums[i - 1] > dp[0][j - 1]:
                dp[0][j] = dp[0][j - 1] + nums[i - 1]
                path[0][j] = path[0][j - 1] + ['H']
            elif nums[i - 1] > dp[1][j - 1]:
                dp[0][j] = dp[1][j - 1] + nums[i - 1]
                path[0][j] = path[1][j - 1] + ['H']

            if nums[i - 1] > dp[1][j]:
                dp[1][j] = dp[0][j - 1] + nums[i - 1]
                path[1][j] = path[0][j - 1] + ['M']
            elif nums[i - 1] > dp[1][j - 1]:
                dp[1][j] = dp[1][j - 1] + nums[i - 1]
                path[1][j] = path[1][j - 1] + ['M']

    return dp[n][n], path[0][n], path[1][n]

