import sys

def get_prizes(n, digits):
    """Compute the maximum prize for each possible prefix of the numbers."""
    prizes = [[0] * (n + 1) for _ in range(2)]
    for i in range(2 * n):
        digit = int(digits[i])
        for j in range(n - 1, -1, -1):
            for k in range(2):
                if j == 0:
                    prizes[k][j] = max(prizes[1 - k][j], prizes[k][j] + digit)
                else:
                    best = 0
                    for l in range(10):
                        if l != digit:
                            best = max(best, prizes[k][j - 1] + l * (10 ** (j - 1)))
                    prizes[k][j] = max(prizes[1 - k][j - 1], best)
    return prizes


def get_sequence(n, digits, prizes):
    """Reconstruct the optimal sequence of moves."""
    sequence = [''] * n
    left, right = 0, n - 1
    for i in range(n):
        best = 0
        for j in range(2):
            if j == 0:
                best = max(best, prizes[j][left])
            else:
                best = max(best, prizes[j][right])
        if best == prizes[0][left]:
            sequence[left] = 'H'
            left += 1
        else:
            sequence[right] = 'M'
            right -= 1
    return ''.join(sequence)


def main():
    n = int(input())
    digits = input()
    prizes = get_prizes(n, digits)
    sequence = get_sequence(n, digits, prizes)
