        for j in range(n):
            if (i >> j) & 1:
                b[j] = a[j]

        # Check if the sum of elements at the same positions in a and b is different
        if all(a[j] != b[j] for j in range(n)):
            return b

        # If not, try to swap the last two elements
        if n > 2:
            b[-1], b[-2] = b[-2], b[-1]
            if all(a[j] != b[j] for j in range(n)):
                return b

    # If no solution is found, return -1
    return [-1]

