import sys

def solve(n, a):
    if n == 1:
        return ' '.join(map(str, a))

    dp = [[False] * (1 << n) for _ in range(1 << n)]
    dp[0][0] = True
    for i in range(1, 1 << n):
        for j in range(i.bit_length(), n):
            if not i & (1 << j):
                continue
            for k in range(i.bit_length(), n):
                if not i & (1 << k):
                    continue
                dp[i][i | (1 << j)] |= dp[i][i | (1 << k)]

    def dfs(mask, pos):
        if mask == (1 << n) - 1:
            return []
        res = []
        for i in range(pos, n):
            if not mask & (1 << i):
                continue
            if dp[mask][mask ^ (1 << i)]:
                res.append(a[i])
                dfs(mask ^ (1 << i), i)
                break
        return res

    ans = dfs(0, 0)
    if len(ans) < n:
        return -1
    return ' '.join(map(str, ans))

