import math

def minimal_n(p):
    if p == 0:
        return 0
    n = 1
    while True:
        if n * (n - 1) // 2 >= p:
            return n
        n += 1

def max_unidirectional(p, n):
    if p == 0:
        return 0
    # Find the minimal m where C(m,2) >= p
    m = 1
    while True:
        if m * (m - 1) // 2 >= p:
            break
        m += 1
    # Now, construct the graph: m nodes form a cycle contributing C(m,2) bidirectional pairs
    # The remaining n - m nodes form a complete DAG
    # The unidirectional pairs are:
    # 1. Edges from outside to cycle: each contributes 1
    # 2. Edges within outside: complete DAG, which has (n-m)*(n-m-1)/2 edges
    # 3. Any edges from cycle to outside? No, because outside can't reach cycle.
    # So total unidirectional pairs is (n - m)*(n - m - 1)/2 + (n - m)*m
    return (n - m) * (n - m - 1) // 2 + (n - m) * m

