import math

def solve(p):
    if p == 0:
        return [0, 0]

    n = int(math.sqrt(2 * p)) + 1
    while True:
        pairs = n * (n - 1) // 2
        if pairs >= p:
            break
        n += 1

    unidirectional_pairs = 0
    for i in range(1, n):
        if pairs - i * (i - 1) // 2 >= p - pairs:
            unidirectional_pairs += i * (n - i)
            break

    return [n, pairs - p + unidirectional_pairs]

