    num = 1
    for i in range(N):
        for j in range(N):
            grid[i][j] = num
            num += 1

    # Function to simulate the rook's path
    def rook_path(grid):
        visited = set()
        current = (0, 0)
        visited.add(current)
        path = []
        while True:
            row, col = current
            min_val = None
            min_cell = None
            # Check same row
            for j in range(N):
                if j != col and (j, row) not in visited:
                    if grid[j][row] < (min_val or float('inf')):
                        min_val = grid[j][row]
                        min_cell = (j, row)
            # Check same column
            for i in range(N):
                if i != row and (i, col) not in visited:
                    if grid[i][col] < (min_val or float('inf')):
                        min_val = grid[i][col]
                        min_cell = (i, col)
            if min_cell is None:
                break
            path.append(min_cell)
            visited.add(min_cell)
            current = min_cell
        return path

    # Function to simulate the queen's path
    def queen_path(grid):
        visited = set()
        current = (0, 0)
        visited.add(current)
        path = []
        while True:
            row, col = current
            candidates = []
            # Check all 8 directions
            for dr in [-1, 0, 1]:
                for dc in [-1, 0, 1]:
                    if dr == 0 and dc == 0:
                        continue
                    nr, nc = row + dr, col + dc
                    if 0 <= nr < N and 0 <= nc < N:
                        cell = (nr, nc)
                        if cell not in visited:
                            candidates.append((cell[0], cell[1]))
            if not candidates:
                break
            # Find the cell with the smallest number
            min_val = None
            min_cell = None
            for (i, j) in candidates:
                val = grid[i][j]
                if min_val is None or val < min_val:
                    min_val = val
                    min_cell = (i, j)
            path.append(min_cell)
            visited.add(min_cell)
            current = min_cell
        return path

    # Calculate vuns for both
    rook_path = rook_path(grid)
    queen_path = queen_path(grid)

    # Count vuns
    def count_vuns(path):
        count = 0
        visited = set()
        for step in path:
            cell = step
            if cell in visited:
                count += 1
            visited.add(cell)
        return count

    rook_vuns = count_vuns(rook_path)
    queen_vuns = count_vuns(queen_path)

    if rook_vuns < queen_vuns:
