    while current_row < N and current_col < N:
        if direction == 0:
            if board[current_row][current_col + 1] == 0:
                current_col += 1
                continue
        elif direction == 1:
            if board[current_row + 1][current_col] == 0:
                current_row += 1
                continue
        elif direction == 2:
            if board[current_row][current_col - 1] == 0:
                current_col -= 1
                continue
        else:  # direction == 3
            if board[current_row - 1][current_col] == 0:
                current_row -= 1
                continue

        # Place the number
        board[current_row][current_col] = num
        num -= 1
        if num == 0:
            break

        # Change direction
        direction = (direction + 1) % 4

    # Print the board
    for row in board:
