def print_board(n):
    if n == 1:
        print("1")
        return

    # Create a board with numbers from 1 to n*n in spiral order
    board = [[0]*n for _ in range(n)]
    num = 1
    top, bottom, left, right = 0, n-1, 0, n-1
    while num <= n*n:
        for i in range(left, right+1):
            board[top][i] = num
            num += 1
        top += 1
        for i in range(top, bottom+1):
            board[i][right] = num
            num += 1
        right -= 1
        for i in range(right, left-1, -1):
            board[bottom][i] = num
            num += 1
        bottom -= 1
        for i in range(bottom, top-1, -1):
            board[i][left] = num
            num += 1
        left += 1

    # Move the number 2 to the top-left corner
    temp = board[0][0]
    board[0][0] = board[n-1][0]
    board[n-1][0] = temp

    # Print the board
    for i in range(n):
        for j in range(n):
            print(board[i][j], end=' ')
        print()

