    board[0][n-1], board[n-1][0] = board[n-1][0], board[0][n-1]

    # adjust values to be consecutive
    flat = [num for row in board for num in row]
    new_flat = sorted(flat)
    diff = [new_flat[i] - new_flat[i-1] for i in range(1, len(new_flat))]
    if min(diff) > 1:
        return None

    # fix the first row
    for i in range(n-1):
        board[0][i+1] = board[0][i] + 1

    # fill in the rest of the board
    for i in range(1, n):
        for j in range(n):
            if j > 0:
                board[i][j] = board[i][j-1] + 1
            else:
