import sys

def get_input():
    return sys.stdin.readline().strip()

def get_ints():
    return list(map(int, get_input().split()))

def solve(n, mod):
    # Create a 2D array dp to store the number of permutations
    dp = [[[0 for _ in range(n + 1)] for _ in range(n + 1)] for _ in range(n + 1)]

    # Initialize the base case where n = 1
    dp[1][0][1] = 1

    # Iterate over all possible values of k and i
    for k in range(1, n + 1):
        for i in range(k + 1):
            # If k is equal to i, then we have already counted all permutations
            if k == i:
                continue
            # Calculate the number of inversions for this permutation
            inversions = (k - 1) * (k - i) % mod
            # Update the dp array with the number of permutations
            for j in range(i + 1, k + 1):
                dp[k + 1][j][inversions] += dp[k][i][inversions]
                dp[k + 1][j][inversions] %= mod

    # Initialize the result
    result = 0
    # Iterate over all possible values of k and i
    for k in range(1, n + 1):
        for i in range(k + 1):
            # Calculate the total number of permutations
            total_permutations = 1
            for j in range(1, k + 1):
                total_permutations *= j
                total_permutations %= mod
            # Update the result with the number of permutations that satisfy the conditions
            result += dp[k][i][0] * total_permutations
            result %= mod

    return result

