class SegmentTree:
    def __init__(self, n):
        self.tree = [[float('-inf')] * (n + 1) for _ in range(4)]
        self.lazy = [0] * (n + 1)

    def apply(self, idx, value):
        self.tree[0][idx] += value
        self.tree[1][idx] += 2 * value * idx
        self.tree[2][idx] += value * idx ** 2
        self.tree[3][idx] += value * idx

    def push(self, idx):
        if self.lazy[idx]:
            self.apply(idx << 1, self.lazy[idx])
            self.apply((idx << 1) | 1, self.lazy[idx])
            self.lazy[idx] = 0

    def merge(self, left, right):
        result = [0] * 4
        for i in range(4):
            result[i] = max(left[i], right[i])
        return result

    def update(self, l, r, value):
        self._update(1, 1, len(self.tree[0]) - 1, l, r, value)

    def _update(self, node, start, end, l, r, value):
        if start > r or end < l:
            return
        if start >= l and end <= r:
            self.apply(node, value)
            return
        self.push(node)
        mid = (start + end) >> 1
        self._update(node << 1, start, mid, l, r, value)
        self._update((node << 1) | 1, mid + 1, end, l, r, value)
        self.tree[node] = self.merge(self.tree[node << 1], self.tree[(node << 1) | 1])

    def query(self, l, r):
        return self._query(1, 1, len(self.tree[0]) - 1, l, r)

    def _query(self, node, start, end, l, r):
        if start > r or end < l:
            return float('-inf') * [4]
        if start >= l and end <= r:
            return self.tree[node]
        self.push(node)
        mid = (start + end) >> 1
        left = self._query(node << 1, start, mid, l, r)
        right = self._query((node << 1) | 1, mid + 1, end, l, r)
        return self.merge(left, right)


def solve(n, h, m, restrictions):
    tree = SegmentTree(n)
    for l, r, x in restrictions:
        tree.update(l, r, -x ** 2)
    result = 0
    for i in range(1, n + 1):
        heights = tree.query(i, i)
        max_height = max(heights[0], heights[1] // i, heights[2] // (i ** 2))
        result += max_height ** 2
    return result


def main():
    input_lines = sys.stdin.read().splitlines()
    outputs = []
    index = 0
    while index < len(input_lines):
