            self.apply(idx << 1, self.lazy[idx])
            self.apply((idx << 1) | 1, self.lazy[idx])
            self.lazy[idx] = 0

    def merge(self, left, right):
        result = [0] * 4
        result[0] = max(left[0], right[0])
        result[1] = max(left[1], right[1])
        result[2] = max(left[2], right[2])
        result[3] = max(left[3], right[3])
        return result

    def update_range(self, start, end, value):
        self._update_range(1, 1, len(self.tree[0]) - 1, start, end, value)

    def _update_range(self, node, node_start, node_end, start, end, value):
        if start > node_end or end < node_start:
            return
        if start <= node_start and node_end <= end:
            self.apply(node, value)
            return
        self.push(node)
        mid = (node_start + node_end) // 2
        self._update_range(node << 1, node_start, mid, start, end, value)
        self._update_range((node << 1) | 1, mid + 1, node_end, start, end, value)
        self.tree[node] = self.merge(self.tree[node << 1], self.tree[(node << 1) | 1])

    def query(self, start, end):
        return self._query(1, 1, len(self.tree[0]) - 1, start, end)


    def _query(self, node, node_start, node_end, start, end):
        if start > node_end or end < node_start:
            return float('-inf'), float('-inf'), float('-inf'), float('-inf')
        if start <= node_start and node_end <= end:
            return tuple(self.tree[node])
        self.push(node)
        mid = (node_start + node_end) // 2
        left = self._query(node << 1, node_start, mid, start, end)
        right = self._query((node << 1) | 1, mid + 1, node_end, start, end)
        return self.merge(left, right)


