    for i in range(h + 1):
        dp[0][i] = i ** 2

    for i in range(1, n):
        for j in range(h + 1):
            for k in range(j, -1, -1):
                val = dp[i - 1][k] + j ** 2
                new_val = st.query(i, i)[3] + val
                if new_val > dp[i][j]:
                    dp[i][j] = new_val

    ans = 0
    for i in range(h + 1):
        ans = max(ans, dp[n - 1][i])

    print(ans)


