class SegmentTree:
    def __init__(self, n):
        self.tree = [[float('-inf')] * (n + 1) for _ in range(4)]
        self.lazy = [0] * (n + 1)

    def apply(self, idx, value):
        self.tree[0][idx] += value
        self.tree[1][idx] += 2 * value * idx
        self.tree[2][idx] += 3 * value * idx ** 2
        self.lazy[idx] += value

    def push(self, idx):
        if self.lazy[idx]:
            k = self.lazy[idx]
            self.lazy[idx] = 0
            j = idx << 1
            self.apply(j, k)
            self.apply(j + 1, k)

    def update(self, l, r, x, ql, qr, idx):
        if qr < ql or l > r:
            return
        if ql <= l and r <= qr:
            self.apply(idx, x)
            return
        mid = (l + r) >> 1
        self.push(idx)
        self.update(l, mid, x, ql, qr, idx << 1)
        self.update(mid + 1, r, x, ql, qr, idx << 1 | 1)
        self.tree[0][idx] = min(self.tree[0][idx << 1], self.tree[0][idx << 1 | 1])
        self.tree[1][idx] = min(self.tree[1][idx << 1], self.tree[1][idx << 1 | 1])
        self.tree[2][idx] = min(self.tree[2][idx << 1], self.tree[2][idx << 1 | 1])

    def query(self, l, r, ql, qr, idx):
        if qr < ql or l > r:
            return float('inf')
        if ql <= l and r <= qr:
            return self.tree[2][idx]
        mid = (l + r) >> 1
        self.push(idx)
        return min(
            self.query(l, mid, ql, qr, idx << 1),
            self.query(mid + 1, r, ql, qr, idx << 1 | 1)
        )

