        max_height = max(max_height, tree.query(i, i)[3])

    profit = 0
    for i in range(1, n + 1):
        height = min(max_height, h)
        profit += height ** 2
        max_height -= 1
        tree.update(i, i, height ** 2)

    print(profit)


