def main():
    n, k = map(int, sys.stdin.readline().split())
    a = list(map(int, sys.stdin.readline().split()))

    # Precompute max_val[i][j] as the maximum from a[i] to a[j]
    max_val = [[0] * n for _ in range(n)]
    for i in range(n):
        current_max = a[i]
        max_val[i][i] = current_max
        for j in range(i+1, n):
            if a[j] > current_max:
                current_max = a[j]
            max_val[i][j] = current_max

    # Initialize DP table
    dp = [[0] * (k+1) for _ in range(n)]

    for i in range(n):
        for j in range(1, min(k, i+1)+1):
            if j == 1:
                dp[i][j] = max_val[0][i]
            else:
                dp[i][j] = 0
                for m in range(i):
                    if j-1 <= m + 1:
                        current = dp[m][j-1] + max_val[m+1][i]
                        if current > dp[i][j]:
                            dp[i][j] = current

    # Now, reconstruct the t array
    t = [0] * k
    current_end = n - 1
    for j in range(k-1, -1, -1):
        for m in range(current_end, -1, -1):
            if dp[m][j+1] != 0:
                t[j] = m - t[j-1] if j > 0 else m + 1
                current_end = m
                break

    # Convert t to 1-based indices for output
    t = [t[i]+1 for i in range(k)]

    print(sum(max_val[t[i]-1][t[i]] for i in range(k)))
    print(' '.join(map(str, t)))

