import math

def gcd(a, b):
    """Compute the greatest common divisor of a and b"""
    while b:
        a, b = b, a % b
    return a

def lcm(a, b):
    """Compute the lowest common multiple of a and b"""
    return a * b // gcd(a, b)

def solve(a, b):
    """Find the smallest non-negative integer k such that the least common multiple of a+k and b+k is the smallest possible"""
    if a == b:
        return 0

    # Find the greatest common divisor of a and b
    g = gcd(a, b)

    # If the greatest common divisor is not 1, we can find a k that makes the least common multiple equal to the product of a and b
    if g != 1:
        return g - (a % g)

    # Otherwise, we need to check all possible values of k
    min_lcm = float('inf')
    best_k = 0
    for k in range(1000000):  # We assume that the answer will not be larger than 1,000,000
        lcm_val = lcm(a + k, b + k)
        if lcm_val < min_lcm:
            min_lcm = lcm_val
            best_k = k

    return best_k

def main():
    """Read input from standard input and print the output to standard output"""
    a, b = map(int, input().split())
    print(solve(a, b))

