def find(parent, x):
    if parent[x] != x:
        parent[x] = find(parent, parent[x])
    return parent[x]

def union(parent, rank, val, x, y, g, l):
    x_root = find(parent, x)
    y_root = find(parent, y)
    if x_root == y_root:
        return
    if rank[x_root] < rank[y_root]:
        x_root, y_root = y_root, x_root
    parent[y_root] = x_root
    if rank[x_root] == rank[y_root]:
        rank[x_root] +=1
    if val[y_root] != 0:
        g_x = g
        l_x = l
        if val[x_root] * val[y_root] != g_x * l_x:
            return False
        if val[y_root] == 0:
            val[y_root] = l_x // g_x
            if val[y_root] % g_x != 0:
                return False
    elif val[x_root] == 0:
        val[x_root] = l_x // g_x
        if val[x_root] % g_x !=0:
            return False
    return True

def build_adjacency_list(edges, n):
    adj = [[] for _ in range(n)]
    for u, v, g, l in edges:
        adj[u].append( (v, g, l) )
        adj[v].append( (u, g, l) )
    return adj

