        l = int(data[idx+3])
        edges[u].append( (v, g, l) )
        edges[v].append( (u, g, l) )
        idx +=4

    visited = [False] * (n+1)
    possible = True
    ans = [0]*(n+1)

    for i in range(1, n+1):
        if not visited[i]:
            q = deque()
            q.append(i)
            visited[i] = True
            component = []
            edges_component = []
            while q:
                u = q.popleft()
                component.append(u)
                for (v, g, l) in edges[u]:
                    if not visited[v]:
                        visited[v] = True
                        q.append(v)
                        edges_component.append( (u, v, g, l) )
            D = 0
            for (u, v, g, l) in edges_component:
                D = gcd(D, g)
            if D == 0:
                D = 1
            divisors = get_divisors(D)
            found = False
            for d in divisors:
                valid = True
                k = {}
                for (u, v, g, l) in edges_component:
                    product = (g * l) 
                    if d*d > product:
                        valid = False
                        break
                    if product % (d*d) != 0:
                        valid = False
                        break
                if not valid:
                    continue

                k[i] = 1
                q = deque()
                q.append(i)
                while q and valid:
                    u = q.popleft()
                    for (v, g, l) in edges[u]:
                        if v == u:
                            continue
                        if v not in k:
                            product = (g * l) // (d * d)
                            if product % k[u] != 0:
                                valid = False
                                break
                            candidate = product // k[u]
                            if math.gcd(k[u], candidate) != (g // d):
                                valid = False
                                break
                            k[v] = candidate
                            q.append(v)
                if valid:
                    for node in component:
                        ans[node] = d * k[node]
                    found = True
                    break
            if not found:
                possible = False
                break
    if possible:
